import React, {useState, useEffect} from 'react'; 
//allows us to use sideEffects any work outside of the component
//setting up event listener, updating title etc
//cleanup function
//second parameter 
//by default runs after every re-render

  const UseStateCounter = () => {  //in this example I would like to change the title instead of react app
    const[value, setValue] = useState(0)

    useEffect(() => { //we pass in the cb function, va functiona dupa fiecare render
      console.log('call useEffect');
      document.title = `New Messages(${value})`
    })
  return (
  <div className='container'>
    <h1> {value}</h1>
    <button onClick={() => setValue(value + 1)} > Increase value</button>

  </div>
    )
  };

export default UseStateCounter;
