// component cu clasa
//rcc
import React, { Component } from 'react'

export default class Ccomponents extends Component {
  //contructor - creeaza schema pt a scrie starea   rconst

  constructor(props) {
    super(props)
  
    this.state = {
       name:"Andrei"
    }
    this.handClick = this.handClick.bind(this); //bind activeaza btn si functia
  }
 
  handClick(){
    this.setState({
      visibility:false
      
    })
  }

  render() {
    return (
      <div>
       <h1> Class component {this.state.name} </h1>
       <button onClick={this.handClick}>Btn</button>
      </div>
    )
  }
}
