// component cu clasa
//rcc
import React, { Component } from 'react'

export default class Ccomponents extends Component {
  //contructor - creeaza schema pt a scrie starea   rconst

  constructor(props) {
    super(props)
  
    this.state = {
      visibility:false  //sa nu fie vizibil

    }
    this.handClick = this.handClick.bind(this); //bind activeaza btn si functia
  }
 
  handClick(){
    this.setState(state => ({
      visibility: !state.visibility
    }));
  }

  render() {
    
  }
}
