import React from 'react';
import { useParams } from 'react-router'; // trebuie sa il importam
import {getRecipeById} from "../data";
import {useState, useEffect} from "react";

 const Recipe = () => {
   const {RecipeId} = useParams() // ne va afisa id-ul care e dupa recipe

   const [recipeTitle, setRecipeTitle] = useState(getRecipeById(RecipeId));
   const [recipeIngredients, setRecipeIngredients] = useState(getRecipeById(RecipeId));
   const [recipeInstructions, setRecipeInstructions] = useState(getRecipeById(RecipeId));
   const [recipeImage, setRecipeImage] = useState(getRecipeById(RecipeId));


  return (
    <div>
      <h1> Recipe {RecipeId}</h1>
    </div>
  )
}

export default Recipe;
