import React from 'react';
import { useParams } from 'react-router'; // trebuie sa il importam
import {getRecipeById} from "../data";
import {useState, useEffect} from "react";

 const Recipe = () => {
   const {recipeId} = useParams() // ne va afisa id-ul care e dupa recipe

   const [recipeTitle, setRecipeTitle] = useState("");
   const [recipeIngredients, setRecipeIngredients] = useState([]);
   const [recipeInstructions, setRecipeInstructions] = useState("");
   const [recipeImage, setRecipeImage] = useState("");

   useEffect(() => {
     let recipe = getRecipeById(recipeId).title
     setRecipeTitle(recipe.title);
     setRecipeIngredients(recipe.ingredients);
     setRecipeInstructions(recipe.instructions);
     setRecipeImage(recipe.image);
   
   }, [recipeId]);


  return (
    <div>
      <h2> {recipeTitle} </h2>
      <h2> Ingredients: </h2>
      <ul>
        {recipeIngredients.map(element => (
          <li key="index"> {element.name} {element.quantity} </li>
        ))};
      </ul>
      <h2> Instructions: </h2>
      <p> {recipeInstructions} </p>
        
    </div>
  )
}

export default Recipe;
