import './App.css';
import React, {useState} from 'react';

const UseStateBasic = () => {
const [text, setText] = useState('Random Text');
  const handleClick = () => {
    if (text === 'Random Text') {
      setText = ("Hello World!")
    } else {
      setText = ("Random Text")
    }

  }
  return (
    <div>
      <h1> {text} </h1>
      <button type="button" onClick={handleClick}> Change text </button>
    </div>
  )
}

export default UseStateBasic;
