import React, {useState} from 'react';

 const UseStateBasic = () => {
   const [text, setText] = useState ('random text');
   const handleClick = () => {
     if (text === ('random text')) {
      setText("Hello World!")
     } else {
      setText('random text')
     }

   }

  return (
    <div>
      <h1> {text} </h1>
      <button type="button" onClick={handleClick}>
      Change text
      </button>
    </div>
  )
}
export default UseStateBasic;
